import React, { useState} from 'react'
import {
    ListGroup, Row,
    Col, Button, Container, Modal, Alert
} from 'react-bootstrap'
import { Link } from "react-router-dom"
import { AiFillCheckCircle} from "react-icons/ai";



function ListUser(props) {

    const users = props.list || []
    const [usuario, setUsuario] = useState({})

    const [show, setShow] = useState(false);
    const [successDelete, setSuccessDelete] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const listUser1 = () => {
        return users.map((user) => {
            return (
                <ListGroup.Item key={user.id}>
                    <Row className="itemTask">
                         <Col xs={6} md={8}>
                            {user.id}
                        </Col>
                        <Col xs={6} md={8}>
                            {user.name}
                        </Col>

                        <Col xs={6} md={8}>
                            {user.age}
                        </Col>

                        <Col xs={6} md={8}>
                            {user.document}
                        </Col>

                        <Col xs={6} md={8}>
                            {user.tel}
                        </Col>

                        <Col xs={6} md={8}>
                            {user.state}
                        </Col>
                        <Col>

                            <Button className="mx-3" variant="secondary"
                                onClick={() => {
                                    setUsuario(user)

                                }}>
                                Editar
                            </Button>
                            <Button className="mx-3" variant="danger"
                                onClick={() => {
                                    setUsuario(user)

                                }}>
                                Deletar
                            </Button>
                        </Col>
                    </Row>

                </ListGroup.Item>
            )
        })
    }

    return (
        <>
            <Container>
                {
                    successDelete
                        ?
                        <Alert key='success' variant='success'>
                            <AiFillCheckCircle size="30" /> Item apagado com suceso
                        </Alert>
                        :
                        ''
                }
                <h1 className="text-center">
                    Lista de clientes </h1>
                <Row>
                    <Col>
                        <ListGroup variant="flush">
                      {
                      listUser1()
                      }
                        </ListGroup>
                    </Col>
                </Row>

                {/* //modal delete */}
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Excluir Cliente</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Deseja deletar Cliente: <strong>{user.name}</strong></Modal.Body>
