import React, { useState } from 'react'
import {  ListGroup, Row,
    Col, Button, Container, Modal, Alert} from 'react-bootstrap'
    import { AiFillCheckCircle} from "react-icons/ai"; 
function ListUser(props) {

    const users = props.list || []
    const [usuario, setUsuario] = useState({})

    const [show, setShow] = useState(false);
    const [successDelete, setSuccessDelete] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const listUser1 = () => {
        return users.map((user) => {
            return (
                <ListGroup.Item key={user.id}>
                    <Row className="itemTask">
                        <Col xs={6} md={8}>
                            ID : {user.id}
                        </Col>
                        <Col xs={6} md={8}>
                            NOME: {user.name}
                        </Col>

                        <Col xs={6} md={8}>
                            IDADE: {user.age}
                        </Col>

                        <Col xs={6} md={8}>
                            CPF:  {user.document}
                        </Col>

                        <Col xs={6} md={8}>
                            TELEFONE : {user.tel}
                        </Col>

                        <Col xs={6} md={8}>
                            UF:  {user.state}
                        </Col>
                        <Col>

                            <Button className="mx-3" variant="secondary"
                                onClick={() => {
                                    setUsuario(user)

                                }}>
                                Editar
                            </Button>
                            <Button className="mx-3" variant="danger"
                                onClick={() => {                             
                                    setUsuario(user)
                                    handleShow()
                                }}>
                                Deletar
                            </Button>
                        </Col>
                    </Row>

                </ListGroup.Item>
            )
        })
    }

    return (
        <>
        <Container>
        {
                    successDelete
                        ?
                        <Alert key='success' variant='success'>
                            <AiFillCheckCircle size="30" /> Item apagado com suceso
                        </Alert>
                        :
                        ''
                }
            <Row>
                <Col>
                    <ListGroup variant="flush">
                        {listUser1()}
                    </ListGroup>
                </Col>
            </Row>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Excluir Cliente</Modal.Title>
                </Modal.Header>
                <Modal.Body>Deseja deletar Cliente: <strong>{usuario.name}</strong></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="danger" onClick={() => {
                        props.delete(usuario.id)
                        handleClose()
                        setSuccessDelete(true)
                        setTimeout(
                            () => {
                                setSuccessDelete(false)
                            }, 3000)
                    }}>
                        Apagar
                    </Button>
                </Modal.Footer>
            </Modal>
            </Container>
        </>

    )
}

export default ListUser
